mapper.select:
select * from tbl_member
where user_id = #{user_id}
and user_pw = #{user_pw}

mapper.update:
update tbl_member set
	user_name = #{user_name},
	user_pw = #{user_pw},
	user_phone = #{user_phone}
where user_id = #{user_id}

update tbl_member set
	is_out = 'Y'
where user_id = #{user_id}
